floor = 0
index = 0
has_entered_basement = false
input.each_char do |char|
  index += 1

  if char == '('
    floor += 1
  elsif char == ')'
    floor -= 1
  end
  
  if floor < 0 and !has_entered_basement
    has_entered_basement = true
    puts "Santa first entered the basement on instruction #{index}"
  end
end
puts "Santa is at #{floor}"